import React, { useState } from 'react';
import './login.css';
import axios from 'axios';

function AuthForm({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
  
    // 添加验证逻辑
    const usernameRegex = /^[a-zA-Z0-9]{6,12}$/; // 示例正则表达式，用于用户名验证
    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{8,}$/; // 示例正则表达式，用于密码验证
  
    if (!username || !usernameRegex.test(username)) {
      setError('用户名不能为空且为6-12个字符，包含字母和数字');
      return;
    }
  
    if (!password || !passwordRegex.test(password)) {
      setError('密码不能为空且为8个字符以上，包含至少一个大写字母、一个小写字母和一个数字');
      return;
    }
  
    if (isLogin) {
      console.log('登录:', { username, password });
      axios({
        method: 'post',
        url: 'http://localhost:2531/login',
        data: {
          email: e.id.toString(),
          password: e.password
        }
      }).then((res) =>{
        if(res.data.succuss){
          console.log(res.data);

        }else{
          console.log(res.data);
        }
      })
      // 在此处添加登录逻辑
    } else {
      console.log('注册:', { username, email, password });
      // 在此处添加注册逻辑
    }

  };

  return (
    <div className="auth-form">
      <h2>{isLogin ? '登录' : '注册'}</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>用户名：</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="请输入用户名"
          />
        </div>
        {!isLogin && (
          <div className="form-group">
            <label>邮箱：</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="请输入邮箱"
            />
          </div>
        )}
        <div className="form-group">
          <label>密码：</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="请输入密码"
          />
        </div>
        {error && <p className="error">{error}</p>}
        <button type="submit" onClick={onLogin}>{isLogin ? '登录' : '注册'}</button>
      </form>
      <button className="toggle-btn" onClick={() => setIsLogin(!isLogin)}>
        切换到{isLogin ? '注册' : '登录'}
      </button>
    </div>
  );
}

export default AuthForm;
