import './resource.css';
import React, { useState } from 'react';
import { Link, Outlet, useNavigate } from 'react-router-dom';
import ResourceCard from './resourceCard';
import { Menu, Dropdown } from 'antd';
import { DownOutlined } from '@ant-design/icons';

const Resource = () => {
    const [resources] = useState([  
        {
            name: '选择题 1',
            title: '数学',
            source: '教材A',
            id: 1
        },
        {
            name: '选择题 2',
            title: '语文',
            source: '教材B',
            id: 2
        },
        {
            name: '选择题 3',
            title: '英语',
            source: '教材C',
            id: 3
        }
    ]);

    const [selectedCategory, setSelectedCategory] = useState('全部');
    const navigate = useNavigate(); 

    // 处理分类菜单选择
    // const menu = (
    //     <Menu onClick={({ key }) => setSelectedCategory(key)}>
    //         <Menu.Item key="全部">全部</Menu.Item>
    //         <Menu.Item key="数学">数学</Menu.Item>
    //         <Menu.Item key="语文">语文</Menu.Item>
    //         <Menu.Item key="英语">英语</Menu.Item>
    //     </Menu>
    // );

    // 过滤资源列表
    const filteredResources = resources.filter(resource =>
        selectedCategory === '全部' || resource.title === selectedCategory
    );

    // 点击资源卡片时的处理函数
    const handleCardClick = (resourceId) => {
        console.log('点击了资源卡片:', resourceId);  // 处理点击事件，跳转到详情页
        navigate(`/main/question/${resourceId}`,{});
    };

    return (
        <div>
            <div className="menu">
                <Link to='../course/management'>课程管理</Link>
                <Link to='../resource'>资源管理</Link>
            </div>

            {/* <div className="category-bar">
                <span>选择分类:</span>
                <Dropdown >
                    <a className="ant-dropdown-link" onClick={e => e.preventDefault()}>
                        {selectedCategory} <DownOutlined />
                    </a>
                </Dropdown>
            </div> */}

            <div className="resource-list">
                {filteredResources.map((resource, index) => (
                    <ResourceCard 
                        key={index}
                        name={resource.name}
                        title={resource.title}
                        source={resource.source}
                        id={resource.id}
                    />
                ))}
            </div>

            <Outlet />
        </div>
    );
};

export default Resource;
