import axios from 'axios';

// 创建 axios 实例
const axiosInstance = axios.create({
    baseURL: 'http://localhost:5000/api', // 后端接口的基础路径
    timeout: 5000, // 请求超时设置
    headers: {
        'Content-Type': 'application/json',
    },
});


export default axiosInstance;
