import styles from "./interaction.css"
import InteractionCard from "./interactionCard"


class Interaction extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            num: 3,
            questions: []
        }
    }

    render() {

        const num = this.state.num

        return(

            

            <div className="container">
            <div className="header-container">
                <h2>课堂互动</h2>
            </div>
                <div  style={{ height: "500px", overflowY: "scroll", padding:"20px" }}>
                    <div className="interaction-card" >
                        <div>
                            <div className="interaction-type">单选题</div>
                            <div className="interaction-description">描述：这是一个单选题的示例。</div>
                        </div>
                        <div className="status answered">已作答</div>
                    </div>
                </div>
    
            </div>
        )
    }
}

export default Interaction
