import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, Title, Tooltip, Legend, BarElement, CategoryScale, LinearScale } from 'chart.js';
import './answer.css'
import { selectRecord } from '../api/answer';

// 注册 Chart.js 所需的组件
ChartJS.register(Title, Tooltip, Legend, BarElement, CategoryScale, LinearScale);

const Answer = () => {
    const h_w_id = localStorage.getItem('h_w_id');
  // 初始化题目、选项和回答分布的state
  const [question, setQuestion] = useState('');
  const [options, setOptions] = useState([]);
  const [answerDistribution, setAnswerDistribution] = useState([]);

  // 使用 useEffect 模拟数据初始化
  useEffect(async () => {
    const h_w = await selectRecord(h_w_id);
    const data = h_w.data[0];
    // 模拟从API获取数据或从父组件传入数据
    const initialQuestion = "你最喜欢的编程语言是？";
    const initialOptions = ["JavaScript", "Python", "Java", "C++"];
    const calculateAnswerDistribution = (data) => {
        // 初始化选项计数
        const counts = { a: 0, b: 0, c: 0, d: 0 };
    
        // 统计每个选项的数量
        data.forEach(item => {
            if (counts[item.answer] !== undefined) {
                counts[item.answer]++;
            }
        });
    
        // 转换为数组 [a的数量, b的数量, c的数量, d的数量]
        return ['a', 'b', 'c', 'd'].map(option => counts[option]);
    };
    
    const initialAnswerDistribution = calculateAnswerDistribution(data);
    

    // 设置数据
    setQuestion(initialQuestion);
    setOptions(initialOptions);
    setAnswerDistribution(initialAnswerDistribution);
  }, []);

  // 创建条形图所需的数据格式
  const chartData = {
    labels: options, // 选项
    datasets: [
      {
        data: answerDistribution, // 答案分布
        backgroundColor: '#36A2EB', // 条形图的颜色
        borderColor: '#36A2EB',
        borderWidth: 1,
        barThickness: 15, // 控制条形图的粗细
      },
    ],
  };

  // 配置图表选项（如缩小图表大小）
  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false, // 允许图表容器尺寸发生变化
    plugins: {
      legend: {
        position: 'top',
      },
    },
    scales: {
      x: {
        beginAtZero: true, // 确保从0开始显示
      },
      y: {
        beginAtZero: true, // 确保从0开始显示
      },
    },
  };

  return (
    <div className="answer-container">
      <h2>{question}</h2>
      <div className="options">
        {options.map((option, index) => (
          <div key={index} className="option">
            <input type="radio" name="answer" id={`option${index}`} disabled />
            <label htmlFor={`option${index}`}>{option}</label>
          </div>
        ))}
      </div>

      <div className="chart">
        <h3>回答情况分布</h3>
        <div style={{ height: '250px', width: '50%' }}>
          <Bar data={chartData} options={chartOptions} />
        </div>
      </div>
    </div>
  );
};

export default Answer;
