import React from "react"
import styles from "./interaction.css"
import InteractionCard from "./interactionCard"


class Interaction extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            num: 3,
            questions: [
                {
                    type:"单选题",
                    description:"这是一个单选题的示例。",
                    status:"answered"
                },
                {
                    type:"多选题",
                    description:"这是一个多选题的示例。",
                    status:"answered"
                },
                {
                    type:"单选题",
                    description:"这是一个单选题的示例。",
                    status:"answered"
                }
            ]
        }
    }

    render() {

        const num = this.state.num

        return(
            <div>
                {questions.slice(0, num).map((question, index) => (
                <InteractionCard
                    key={index}
                    type={question.type}
                    description={question.description}
                    status={question.status}
                />
                ))}
            </div>
        )
    }
}

export default Interaction
