import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './courseList.css';
import { getTeacherCourse, getStudentCourse } from '../api/course';
import { Button } from 'antd'; // 引入 Button 组件
import { addCourse } from '../api/course';

const CourseList = () => {
    const navigate = useNavigate();
    const Id = localStorage.getItem('user');
    const [courses, setCourse] = useState([]);
    const TorS = Id?.substring(0, 2) === '11' ? 1 : (Id?.substring(0, 2) === '21' ? 0 : null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                if (TorS === 0) { // 如果是教师
                    const response = await getTeacherCourse(Id);
                    console.log(response);
                    setCourse(response[0]); // 更新课程列表
                } else if (TorS === 1) { // 如果是学生
                    const response = await getStudentCourse(Id);
                    console.log(response);
                    setCourse(response[0]); // 更新课程列表
                }
            } catch (err) {
                console.error('获取课程列表失败:', err);
            }
        };
        fetchData();
    }, [Id, TorS]);

    const handleCourseClick = (courseId) => {
        localStorage.setItem('course_id', courseId);
        navigate(`/main/course`); // 跳转到课程详情页，动态传递课程 ID
    };

    // 判断是否为教师
    const isTeacher = TorS === 0;

    // 增加课程的回调函数
    const handleAddCourse = () => {
        // 弹出输入框获取课程名称和内容
        const courseName = prompt('请输入课程名称');
        const courseContent = prompt('请输入课程内容');
        
        // 判断输入是否有效
        if (courseName && courseContent) {
            // 调用 addCourse API 保存课程
            add_Course(courseName, courseContent);
        } else {
            alert('课程名称和内容不能为空');
        }
    };

    // 调用 API 保存课程的函数
    const add_Course = async (name, content) => {
        try {
            const response = await addCourse(name, content, Id); // 假设你已经定义了 addCourseAPI 函数
            if (response.success) {
                alert('课程添加成功');
                // 刷新课程列表
                setCourse(prevCourses => [...prevCourses, response.newCourse]);
            } else {
                alert('课程添加失败');
            }
        } catch (error) {
            console.error('添加课程失败:', error);
        }
    };

    return (
        <div className="course-list">
            {/* 如果是教师，则显示增加课程按钮 */}
            {isTeacher && (
                <div className="add-course-btn">
                    <Button type="primary" onClick={handleAddCourse} style={{ margin: '10px' }}>
                        增加课程
                    </Button>
                </div>
            )}

            {/* 显示课程列表 */}
            {courses && courses.length > 0 ? (
                courses.map((course) => (
                    <div
                        className="course-card"
                        key={course.course_id}
                        onClick={() => handleCourseClick(course.course_id)}
                    >
                        <h2 className="course-title">{course.course_name}</h2>
                        <p className="course-code">课程代码: {course.course_id}</p>
                        <p className="course-description">{course.course_content}</p>
                    </div>
                ))
            ) : (
                <p>没有找到课程</p>
            )}
        </div>
    );
};

export default CourseList;
