import React, { useState, useEffect } from 'react';
import LessonList from './lessonList';
import { getLesson } from '../api/lesson';
import { Button } from 'antd'; // 使用 Ant Design 的按钮样式
import { useNavigate } from 'react-router-dom'; // 用于跳转页面

function Lesson() {
    const [lessons, setLessons] = useState([]);
    const courseId = localStorage.getItem('course_id');
    const userId = localStorage.getItem('user'); // 获取当前用户 ID
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await getLesson(courseId);
                console.log(response);
                setLessons(response[0]); // 更新课程列表
            } catch (err) {
                console.error('获取课程列表失败:', err);
            }
        };
        fetchData();
    }, [courseId]);

    // 判断是否为教师用户
    const isTeacher = userId?.startsWith('21');

    // 添加课堂的回调函数
    const handleAddLesson = () => {
        navigate('/main/addLesson'); // 跳转到添加课堂的页面
    };

    return (
        <div className="app-container">
            <h3>课堂列表</h3>
            
            {/* 如果是教师，则显示按钮 */}
            {isTeacher && (
                <div className="add-lesson-btn">
                    <Button type="primary" onClick={handleAddLesson} style={{marginTrim: '10px'}}>
                        增添课堂
                    </Button>
                </div>
            )}
            
            {/* 课堂列表 */}
            <LessonList lessons={lessons} />
        </div>
    );
}

export default Lesson;
