import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // 导入 useNavigate
import './interactionCard.css';

const InteractionCard = ({ type, description, index }) => {
    // 使用 useState 来管理状态
    const [status, setStatus] = useState('unanswered');
    
    // 获取 navigate 函数
    const navigate = useNavigate();

    // 切换作答状态
    const toggleStatus = () => {
        setStatus('answered');
    };

    // 点击后跳转到指定页面
    const handleClick = () => {
        toggleStatus();
        navigate(`/question${index}`,{});
    };

    return (
        <div className="interaction-card" onClick={handleClick}>
            <div>
                <div className="interaction-type">{type}</div>
                <div className="interaction-description">描述：{description}</div>
            </div>
            <div className={`status ${status === 'answered' ? 'answered' : 'unanswered'}`}>
                {status === 'answered' ? '已作答' : '未作答'}
            </div>
        </div>
    );
};

export default InteractionCard;
