import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './multipleChoiceQuestion.css';
import { insertRecord, selectSingleRecord } from '../api/answer';

const MultipleChoiceQuestion = ({ question, options, correctAnswer }) => {
    const [selectedOption, setSelectedOption] = useState(null);
    const [isCorrect, setIsCorrect] = useState(false); // 答案正确性状态
    const navigate = useNavigate();  // 获取导航功能

    const { id } = useParams();
    const user = localStorage.getItem('user');
    const course_id = localStorage.getItem('course_id');
    const handleOptionClick = (option) => {
        setSelectedOption(option);
        // 将选项字母（如 "A"）映射到选项内容
        const correctOption = options[["A", "B", "C", "D"].indexOf(correctAnswer)];
        if (option === correctOption) {
            setIsCorrect(true);
        } else {
            setIsCorrect(false);
        }
        const insert = async () => {
            try {
                const response = await selectSingleRecord(user, course_id, course_id)
                console.log(response);
                // await insertRecord(id,user,course_id,option);
            } catch (err) {
                console.error('获取题目失败:', err);
            }
        };
    };

    const handleBackClick = () => {
        navigate(-1); // 返回到上一个页面
    };

    return (
        <div className="question-container">
            <h2 className="question">{question}</h2>
            <div className="options-container">
                {options.map((option, index) => (
                    <button
                        key={index}
                        className={`option ${selectedOption === option ? 'selected' : ''}`}
                        onClick={() => handleOptionClick(option)}
                    >
                        {option}
                    </button>
                ))}
            </div>
            {/* 根据答题正确性显示消息 */}
            {selectedOption && (
                <p className="feedback-message">
                    {isCorrect ? '答对了!' : '再试一次!'}
                </p>
            )}

            {/* 返回按钮 */}
            <button className="back-button" onClick={handleBackClick}>
                返回
            </button>
        </div>
    );
};


export default MultipleChoiceQuestion;
