import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './courseList.css';
import { getTeacherCourse } from '../api/course';

const CourseList = () => {
    const navigate = useNavigate();
    const teacherId = localStorage.getItem('user');
    const [courses, setCourse] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await getTeacherCourse(teacherId);
                console.log(response);
                setCourse(response[0]); // 更新课程列表
            } catch (err) {
                console.error('获取课程列表失败:', err);
            }
        };
        fetchData();
    }, [teacherId]); // 依赖 teacherId 以便在其变化时重新加载课程列表

    const handleCourseClick = (courseId) => {
        navigate(`/main/course/${courseId}`); // 动态传递课程 ID 到课程详情页
    };

    return (
        <div className="course-list">
            {courses && courses.length > 0 ? (
                courses.map((course) => (
                    <div
                        className="course-card"
                        key={course.course_id}
                        onClick={() => handleCourseClick(course.course_id)}
                    >
                        <h2 className="course-title">{course.course_name}</h2>
                        <p className="course-code">课程代码: {course.course_id}</p>
                        <p className="course-description">{course.course_content}</p>
                    </div>
                ))
            ) : (
                <p>没有找到课程</p>
            )}
        </div>
    );
};

export default CourseList;
