import React, { Component } from 'react';
import NavigateProvider from './NavigateProvider';  // 引入上述组件

class InteractionCard extends Component {
    constructor(props) {
        super(props);
        this.state = {
            status: 'unanswered',
        };
        this.toggleStatus = this.toggleStatus.bind(this);
        this.handleClick = this.handleClick.bind(this);
    }

    toggleStatus() {
        this.setState({
            status: 'answered',
        });
    }

    handleClick(navigate) {
        // 使用传入的 navigate 函数进行跳转
        navigate(`/question/${this.props.index}`);
    }

    render() {
        const { type, description } = this.props;
        const { status } = this.state;

        return (
            <NavigateProvider>
                {(navigate) => (
                    <div className="interaction-card" onClick={() => this.handleClick(navigate)}>
                        <div>
                            <div className="interaction-type">{type}</div>
                            <div className="interaction-description">描述：{description}</div>
                        </div>
                        <div className={`status ${status === 'answered' ? 'answered' : 'unanswered'}`}>
                            {status === 'answered' ? '已作答' : '未作答'}
                        </div>
                    </div>
                )}
            </NavigateProvider>
        );
    }
}

export default InteractionCard;
