import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { useNavigate } from 'react-router-dom';
import './courseList.css';
import { getTeacherCourse } from '../api/course';

const CourseList =() => {
    const navigate = useNavigate();
    const teacherId = localStorage.getItem('user');
    const [courses, setCourse] = useState()

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await getTeacherCourse(teacherId)
                console.log(response[0])
                setCourse(response[0])
            } catch (err) {
                console.error('获取课程列表失败:', err);
            }
        }
        fetchData();
    })

    const handleCourseClick = () => {
        navigate(`/main/course`);
    };

    return (
        <div className="course-list">
            {courses.map((course) => (
                <div
                    className="course-card"
                    key={course.id}
                    onClick={() => handleCourseClick(course.course_id)}
                >
                    <h2 className="course-title">{course.name}</h2>
                    <p className="course-code">课程代码: {course.course_id}</p>
                    <p className="course-description">{course.course_content}</p>
                </div>
            ))}
        </div>
    );
};

CourseList.propTypes = {
    courses: PropTypes.arrayOf(
        PropTypes.shape({
            id: PropTypes.string.isRequired,
            name: PropTypes.string.isRequired,
            code: PropTypes.string.isRequired,
            description: PropTypes.string.isRequired,
        })
    ).isRequired,
};

export default CourseList;
