import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const Answer = ({ questionId }) => {
  // 模拟数据：假设这就是从后端获取的答题分布和题目信息
  const [answerData, setAnswerData] = useState(null);
  const [question, setQuestion] = useState(null);
  const [options, setOptions] = useState(null);

  useEffect(() => {
    // 模拟数据：题目和选项
    const localQuestionData = {
      question: 'What is the capital of France?',  // 模拟题目
      options: {
        A: 'Paris',
        B: 'London',
        C: 'Rome',
        D: 'Berlin',
      },
    };

    // 模拟答题分布数据
    const localAnswerData = {
      A: 5,
      B: 3,
      C: 7,
      D: 1,
    };

    // 设置题目信息和答题分布数据
    setQuestion(localQuestionData.question);
    setOptions(localQuestionData.options);
    setAnswerData(localAnswerData);
  }, []); // 空依赖数组，组件首次加载时执行一次

  // 如果数据还没有加载完成，显示 loading
  if (!answerData || !question || !options) {
    return <div>Loading...</div>;
  }

  // 图表的数据
  const chartData = {
    labels: ['A', 'B', 'C', 'D'], // 选项
    datasets: [
      {
        label: 'Answer Distribution',
        data: [answerData.A, answerData.B, answerData.C, answerData.D], // 模拟的答题分布数据
        backgroundColor: ['#FF5733', '#33FF57', '#3357FF', '#FF33A1'], // 每个选项的颜色
        borderColor: ['#FF5733', '#33FF57', '#3357FF', '#FF33A1'],
        borderWidth: 1,
      },
    ],
  };

  // 图表的配置
  const chartOptions = {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: 'Answer Distribution for Question ' + questionId,
      },
      tooltip: {
        callbacks: {
          label: function (context) {
            return `${context.label}: ${context.raw} responses`; // 每个选项的响应数量
          },
        },
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Options', // 横坐标标签
        },
      },
      y: {
        title: {
          display: true,
          text: 'Number of Responses', // 纵坐标标签
        },
        beginAtZero: true,
      },
    },
  };

  return (
    <div style={{ width: '80%', margin: '0 auto' }}>
      <h3>Answer Distribution for Question {questionId}</h3>

      {/* 显示题目 */}
      <div>
        <h4>{question}</h4>
      </div>

      {/* 显示选项 */}
      <div>
        <ul>
          <li><strong>A:</strong> {options.A}</li>
          <li><strong>B:</strong> {options.B}</li>
          <li><strong>C:</strong> {options.C}</li>
          <li><strong>D:</strong> {options.D}</li>
        </ul>
      </div>

      {/* 显示答题分布图 */}
      <Bar data={chartData} options={chartOptions} />
    </div>
  );
};

export default Answer;
