import React, { useState, useEffect } from 'react';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, Title, Tooltip, Legend, ArcElement, CategoryScale, LinearScale } from 'chart.js';

// 注册 Chart.js 所需的组件
ChartJS.register(Title, Tooltip, Legend, ArcElement, CategoryScale, LinearScale);

const Answer = () => {
  // 初始化题目、选项和回答分布的state
  const [question, setQuestion] = useState('');
  const [options, setOptions] = useState([]);
  const [answerDistribution, setAnswerDistribution] = useState([]);

  // 使用 useEffect 模拟数据初始化
  useEffect(() => {
    // 模拟从API获取数据或从父组件传入数据
    const initialQuestion = "你最喜欢的编程语言是？";
    const initialOptions = ["JavaScript", "Python", "Java", "C++"];
    const initialAnswerDistribution = [40, 30, 20, 10];

    // 设置数据
    setQuestion(initialQuestion);
    setOptions(initialOptions);
    setAnswerDistribution(initialAnswerDistribution);
  }, []);

  // 创建图表所需的数据格式
  const chartData = {
    labels: options, // 选项
    datasets: [
      {
        data: answerDistribution, // 答案分布
        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#FF9F40'], // 每个选项的颜色
        hoverOffset: 4,
      },
    ],
  };

  return (
    <div className="answer-container">
      <h2>{question}</h2>
      <div className="options">
        {options.map((option, index) => (
          <div key={index} className="option">
            <input type="radio" name="answer" id={`option${index}`} disabled />
            <label htmlFor={`option${index}`}>{option}</label>
          </div>
        ))}
      </div>

      <div className="chart">
        <h3>回答情况分布</h3>
        <Pie data={chartData} />
      </div>
    </div>
  );
};

export default Answer;
