import  './resource.css';
import React ,{Component} from 'react';
import { Link ,Outlet } from 'react-router-dom';
import ResourceCard from './resourceCard';

class Resource extends Component {
    constructor(props) {
        super(props);
        this.state = {
            resources: [{
                name: '选择题 1',
                title: '数学',
                source: '教材A'
            },
            {
                name: '选择题 2',
                title: '语文',
                source: '教材B'
            },
            {
                name: '选择题 3',
                title: '英语',
                source: '教材C'
            }],
            num: 3
        }
    }

    render() {
        const {resources, num} = this.state;

        return (
            <div>
                <div className="menu">
                    <Link to='../course/management'>课程管理</Link>
                    <Link to='../resource'>资源管理</Link>
                </div>
    
                <div className="resource-list">
                    {Array.from({ length: num }, (_, index) => (
                        <ResourceCard
                            key={index}
                            name={`选择题 ${index + 1}`}
                            title="数学"
                            source="教材A"
                        />
                    ))}
                </div>
                <Outlet />
            </div>
        )
    }

}

export default Resource;
