import './resource.css';
import React, { useState, useEffect } from 'react';
import { Link, Outlet, useNavigate } from 'react-router-dom';
import ResourceCard from './resourceCard';
import { Menu, Dropdown } from 'antd';
import { DownOutlined } from '@ant-design/icons';

const Resource = () => {
    const [resources, setResources] = useState([
        {
            name: '',
            title: '',
            source: '',
            id: 0
        },
    ]);

    const teacher_id = localStorage.getItem('teacher_id');

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await selectQ(id);
                const h_w = response
                setContent(h_w[0][0].content)
                setOptions([h_w[0][0].option_a, h_w[0][0].option_b, h_w[0][0].option_c, h_w[0][0].option_d]);
                setCorrectAnswer(h_w[0][0].answer);
            } catch (err) {
                console.error('获取题目失败:', err);
            }

        };


        fetchData();
    }, [id]); 

    const [selectedCategory, setSelectedCategory] = useState('全部');
    const navigate = useNavigate();

    // 使用 items 属性替代 menu
    const menuItems = [
        { key: '全部', label: '全部' },
        { key: '数学', label: '数学' },
        { key: '语文', label: '语文' },
        { key: '英语', label: '英语' },
    ];

    // 点击菜单项时的回调
    const handleMenuClick = (info) => {
        setSelectedCategory(info.key);
    };

    const filteredResources = resources.filter(resource =>
        selectedCategory === '全部' || resource.title === selectedCategory
    );

    return (
        <div>
            <div className="menu">
                <Link to='../course/management'>课程管理</Link>
                <Link to='../resource'>资源管理</Link>
            </div>

            <div className="category-bar">
                <span>选择分类:</span>
                <Dropdown
                    menu={{
                        items: menuItems,
                        onClick: handleMenuClick,
                    }}
                >
                    <a className="ant-dropdown-link" onClick={(e) => e.preventDefault()}>
                        {selectedCategory} <DownOutlined />
                    </a>
                </Dropdown>
            </div>

            <div className="resource-list">
                {filteredResources.map((resource, index) => (
                    <ResourceCard
                        key={index}
                        name={resource.name}
                        title={resource.title}
                        source={resource.source}
                        id={resource.id}
                    />
                ))}
            </div>

            <Outlet />
        </div>
    );
};

export default Resource;
