import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const Answer = ({ questionId }) => {
  const [answerData, setAnswerData] = useState(null);
    questionId = 123;
//   useEffect(() => {
//     // 假设后端 API 返回的是类似这样的数据：{ A: 5, B: 3, C: 7, D: 1 }
//     const fetchAnswerData = async () => {
//       try {
//         const response = await fetch(`/api/answers/${questionId}`);
//         const data = await response.json();
//         setAnswerData(data);
//       } catch (error) {
//         console.error('Error fetching answer data:', error);
//       }
//     };

//     fetchAnswerData();
//   }, [questionId]);

    setAnswerData({ A: 5, B: 3, C: 7, D: 1 });

  if (!answerData) {
    return <div>Loading...</div>;
  }

  const chartData = {
    labels: ['A', 'B', 'C', 'D'],
    datasets: [
      {
        label: 'Answer Distribution',
        data: [answerData.A, answerData.B, answerData.C, answerData.D], // A、B、C、D的选择次数
        backgroundColor: ['#FF5733', '#33FF57', '#3357FF', '#FF33A1'],
        borderColor: ['#FF5733', '#33FF57', '#3357FF', '#FF33A1'],
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: 'Answer Distribution for Question ' + questionId,
      },
      tooltip: {
        callbacks: {
          label: function (context) {
            return `${context.label}: ${context.raw} responses`;
          },
        },
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Options',
        },
      },
      y: {
        title: {
          display: true,
          text: 'Number of Responses',
        },
        beginAtZero: true,
      },
    },
  };

  return (
    <div style={{ width: '80%', margin: '0 auto' }}>
      <h3>Answer Distribution for Question {questionId}</h3>
      <Bar data={chartData} options={chartOptions} />
    </div>
  );
};

export default Answer;
