import React, { useState } from 'react';
import './App.css';
import AuthForm from './AuthForm';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  return (
    <div className="App">
        {isLoggedIn ? (
            <div className="app">
              <Header />
              <div className="main-content">
                <Sidebar />
                <QuizContent />
              </div>
            </div>
        ) : (
          <header className="App-header">
            <AuthForm onLogin={handleLogin} />
          </header>
        )}

    </div>
  );
}

export default App;
