import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './setQuestion.css';
import { Dropdown, Input, Button, Switch } from 'antd';
import { DownOutlined } from '@ant-design/icons';
import { getTeacherCourse } from '../api/course';
import { getLesson } from '../api/lesson';
import { selectQ } from '../api/question';

const SetQuestion = () => {
    // 初始化状态
    const [questionContent, setQuestionContent] = useState('');
    const [options, setOptions] = useState(['', '', '', '']);
    const [correctAnswer, setCorrectAnswer] = useState('A');
    const [selectedCourse, setSelectedCourse] = useState('');
    const [selectedClass, setSelectedClass] = useState('');
    const [isVisible, setIsVisible] = useState(false);
    const [courses, setCourses] = useState([]);
    const [classes, setClasses] = useState([]);
    const [existingQuestion, setExistingQuestion] = useState(null); 

    const teacher_id = localStorage.getItem('user');

    const {id} = useParams()

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await selectQ(id);
    
                // 如果 response[0] 不为空，则更新状态
                if (response[0] && Object.keys(response[0]).length > 0) {
                    setExistingQuestion(response[0]);
                } else {
                    console.log('No data available');
                }
            } catch (err) {
                console.error('Error fetching data:', err);
            }
        };
    
        fetchData();
    }, [id]);

    useEffect(() => {
        const fetchData = async () =>{
            try{
                const response = await getTeacherCourse(teacher_id)
                setCourses(response[0]);
            }catch{
                console.error('Error fetching data');
            }
        }
        fetchData();
    }, [teacher_id]);

    useEffect(() => {
        const fetchData = async () =>{
            try{
                if(selectedCourse!==''){
                    const response = await getLesson(selectedCourse)
                    setClasses(response[0]);
                    console.log(response[0]);
                }
            }catch{
                console.error('Error fetching data');
            }
        }
        fetchData();
    },[selectedCourse]);

    const handleOptionChange = (index, value) => {
        const newOptions = [...options];
        newOptions[index] = value;
        setOptions(newOptions);
    };

    const handleDropdownChange = (setType, value) => {
        setType(value);
    }
    const onSave = () => {};

    const onAdd = (questionData) => {};

    const onDelete = () => {};

    const handleSaveOrAdd = () => {
        const questionData = {
            content: questionContent,
            options,
            correct: correctAnswer,
            course_id: selectedCourse,
            class_id: selectedClass,
            visible: isVisible,
        };
        existingQuestion ? onSave(questionData) : onAdd(questionData);
    };

    return (
        <div className="set-question-container">
            <h2>{existingQuestion ? '编辑题目' : '添加新题目'}</h2>
            <div className="form-group">
                <label>题干：</label>
                <Input.TextArea
                    rows={3}
                    value={questionContent}
                    onChange={(e) => setQuestionContent(e.target.value)}
                    placeholder="请输入题目内容"
                />
            </div>
            <div className="form-group">
                <label>选项：</label>
                {options.map((option, index) => (
                    <Input
                        key={index}
                        value={option}
                        onChange={(e) => handleOptionChange(index, e.target.value)}
                        placeholder={`选项 ${String.fromCharCode(65 + index)}`}
                        className="option-input"
                    />
                ))}
            </div>
            <div className="form-group">
                <label>正确答案：</label>
                <Dropdown
                    menu={{
                        items: ['A', 'B', 'C', 'D'].map((answer) => ({
                            key: answer,
                            label: answer,
                        })),
                        onClick: (info) => setCorrectAnswer(info.key),
                    }}
                >
                    <Button>
                        {correctAnswer} <DownOutlined />
                    </Button>
                </Dropdown>
            </div>
            <div className="form-group">
                <label>所属课程：</label>
                <Dropdown
                    menu={{
                        items: courses.map((course) => ({
                            key: course.course_id,
                            label: course.course_name,
                        })),
                        onClick: (info) => {
                                handleDropdownChange(setSelectedCourse, info.key);
                            }
                    }}
                >
                    <Button>
                        {/* 确保 selectedCourse 不是空值并且存在对应课程 */}
                        {selectedCourse
                            ? courses.find(course => course.course_id.toString() === selectedCourse)?.course_name || '请选择课程'
                            : '请选择课程'}
                        <DownOutlined />
                    </Button>
                </Dropdown>
            </div>
            <div className="form-group">
                <label>所属课堂：</label>
                <Dropdown
                    menu={{
                        items: classes.map((classItem) => ({
                            key: classItem.lesson_id,
                            label: classItem.lesson_name,
                        })),
                        onClick: (info) => handleDropdownChange(setSelectedClass, info.key),
                    }}
                >
                    <Button>
                        {classes.find((cls) => cls.lesson_id.toString() === selectedClass)?.lesson_name || '请选择课堂'}{' '}
                        <DownOutlined />
                    </Button>
                </Dropdown>
            </div>
            <div className="form-group">
                <label>是否可见：</label>
                <Switch checked={isVisible} onChange={(checked) => setIsVisible(checked)} />
            </div>
            <div className="button-group">
                {!existingQuestion ? (
                    <Button type="primary" onClick={handleSaveOrAdd}>
                        添加题目
                    </Button>
                ) : (
                    <>
                        <Button type="primary" onClick={handleSaveOrAdd}>
                            保存题目
                        </Button>
                        <Button danger onClick={onDelete}>
                            删除题目
                        </Button>
                    </>
                )}
            </div>
        </div>
    );
};

export default SetQuestion;
