import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './courseList.css';
import { getTeacherCourse, getStudentCourse, addCourse } from '../api/course';
import { Button, Modal, Input } from 'antd'; // 引入 Ant Design 的 Modal 和 Input

const CourseList = () => {
    const navigate = useNavigate();
    const Id = localStorage.getItem('user');
    const [courses, setCourse] = useState([]);
    const [isModalVisible, setIsModalVisible] = useState(false); // 控制 Modal 显示与隐藏
    const [courseName, setCourseName] = useState('');
    const [courseContent, setCourseContent] = useState('');
    const TorS = Id?.substring(0, 2) === '11' ? 1 : (Id?.substring(0, 2) === '21' ? 0 : null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                if (TorS === 0) { // 如果是教师
                    const response = await getTeacherCourse(Id);
                    console.log(response);
                    setCourse(response[0]); // 更新课程列表
                } else if (TorS === 1) { // 如果是学生
                    const response = await getStudentCourse(Id);
                    console.log(response);
                    setCourse(response[0]); // 更新课程列表
                }
            } catch (err) {
                console.error('获取课程列表失败:', err);
            }
        };
        fetchData();
    }, [Id, TorS]);

    const handleCourseClick = (courseId) => {
        localStorage.setItem('course_id', courseId);
        navigate(`/main/course`); // 跳转到课程详情页，动态传递课程 ID
    };

    // 判断是否为教师
    const isTeacher = TorS === 0;

    // 显示 Modal
    const showModal = () => {
        setIsModalVisible(true);
    };

    // 关闭 Modal
    const handleCancel = () => {
        setIsModalVisible(false);
    };

    // 提交课程的回调函数
    const handleAddCourse = async () => {
        if (courseName && courseContent) {
            try {
                // 调用 API 保存课程
                const response = await addCourseAPI(courseName, courseContent);
                if (response.success) {
                    alert('课程添加成功');
                    // 刷新课程列表
                    setCourse(prevCourses => [...prevCourses, response.newCourse]);
                    setIsModalVisible(false); // 关闭 Modal
                } else {
                    alert('课程添加失败');
                }
            } catch (error) {
                console.error('添加课程失败:', error);
            }
        } else {
            alert('课程名称和课程内容不能为空');
        }
    };

    // 调用 API 保存课程的函数
    const addCourseAPI = async (name, content) => {
        // 模拟添加课程的 API 调用
        addCourse(name, content, Id)
    };

    return (
        <div className="course-list">
            {/* 如果是教师，则显示增加课程按钮 */}
            {isTeacher && (
                <div className="add-course-btn">
                    <Button type="primary" onClick={showModal} style={{ margin: '10px' }}>
                        增加课程
                    </Button>
                </div>
            )}

            {/* 课程列表 */}
            {courses && courses.length > 0 ? (
                courses.map((course) => (
                    <div
                        className="course-card"
                        key={course.course_id}
                        onClick={() => handleCourseClick(course.course_id)}
                    >
                        <h2 className="course-title">{course.course_name}</h2>
                        <p className="course-code">课程代码: {course.course_id}</p>
                        <p className="course-description">{course.course_content}</p>
                    </div>
                ))
            ) : (
                <p>没有找到课程</p>
            )}

            {/* 增加课程的 Modal */}
            <Modal
                title="添加新课程"
                visible={isModalVisible}
                onOk={handleAddCourse}
                onCancel={handleCancel}
            >
                <div>
                    <Input
                        placeholder="请输入课程名称"
                        value={courseName}
                        onChange={(e) => setCourseName(e.target.value)}
                        style={{ marginBottom: '10px' }}
                    />
                    <Input.TextArea
                        placeholder="请输入课程内容"
                        value={courseContent}
                        onChange={(e) => setCourseContent(e.target.value)}
                        rows={4}
                    />
                </div>
            </Modal>
        </div>
    );
};

export default CourseList;
